import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: ''
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const newUser = {
      ...formData,
      role: 'buyer',
      id: Date.now().toString()
    };
    users.push(newUser);
    localStorage.setItem('users', JSON.stringify(users));
    alert('Registration successful! Please log in.');
    navigate('/login');
  };

  return (
    <div className="container py-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <h1 className="text-center display-2 mb-5 fw-bold">Register</h1>
          <form onSubmit={handleSubmit} className="p-5 shadow rounded" style={{ backgroundColor: '#f8f9fa' }}>
            <div className="mb-4">
              <label className="form-label fs-2 mb-3">First Name</label>
              <input
                type="text"
                name="firstName"
                className="form-control form-control-lg fs-3 py-3"
                style={{ height: 'auto' }}
                value={formData.firstName}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-4">
              <label className="form-label fs-2 mb-3">Last Name</label>
              <input
                type="text"
                name="lastName"
                className="form-control form-control-lg fs-3 py-3"
                style={{ height: 'auto' }}
                value={formData.lastName}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-4">
              <label className="form-label fs-2 mb-3">Email</label>
              <input
                type="email"
                name="email"
                className="form-control form-control-lg fs-3 py-3"
                style={{ height: 'auto' }}
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="mb-5">
              <label className="form-label fs-2 mb-3">Password</label>
              <input
                type="password"
                name="password"
                className="form-control form-control-lg fs-3 py-3"
                style={{ height: 'auto' }}
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
            <button 
              type="submit" 
              className="btn btn-primary btn-lg w-100 fs-2 py-3"
              style={{
                transition: 'all 0.3s ease',
                borderRadius: '8px'
              }}
              onMouseOver={(e) => {
                e.currentTarget.style.transform = 'scale(1.02)';
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
              }}
            >
              Register
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
