import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Landing from './pages/Landing';
import Register from './pages/Register';
import Login from './pages/Login';
import Home from './pages/Home';
import ProductDetails from './pages/ProductDetails';
import Cart from './pages/Cart';
import AddProduct from './pages/AddProduct';
import ManageProducts from './pages/ManageProducts';
import Dashboard from './pages/Dashboard';
import Orders from './pages/Orders';
import Navbar from './Navbar';
import Profile from './pages/Profile';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);

  // Fetch products from the API
  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:3008/api/products');
      if (!response.ok) throw new Error('Failed to fetch products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Fetch orders from the API
  const fetchOrders = async () => {
    try {
      const response = await fetch('http://localhost:3008/api/orders');
      if (!response.ok) throw new Error('Failed to fetch orders');
      const data = await response.json();
      setOrders(data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  // Handle user login
  const handleLogin = async (credentials) => {
    try {
      const response = await fetch('http://localhost:3008/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(credentials),
      });
      if (!response.ok) throw new Error('Login failed');
      const data = await response.json();
      setIsLoggedIn(true);
      setUserData(data);
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  // Handle user registration
  const handleRegister = async (userInfo) => {
    try {
      const response = await fetch('http://localhost:3008/api/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userInfo),
      });
      if (!response.ok) throw new Error('Registration failed');
      const data = await response.json();
      setIsLoggedIn(true);
      setUserData(data);
    } catch (error) {
      console.error('Error during registration:', error);
    }
  };

  // Fetch data when user logs in
  useEffect(() => {
    if (isLoggedIn) {
      fetchProducts();
      fetchOrders();
    }
  }, [isLoggedIn]);

  return (
    <Router>
      {isLoggedIn && <Navbar setIsLoggedIn={setIsLoggedIn} />}
      <Routes>
        {/* Public Routes */}
        {!isLoggedIn ? (
          <>
            <Route path="/" element={<Landing />} />
            <Route path="/register" element={<Register handleRegister={handleRegister} />} />
            <Route path="/login" element={<Login handleLogin={handleLogin} />} />
            <Route path="*" element={<Navigate to="/" />} />
          </>
        ) : (
          // Authenticated Routes
          <>
            <Route path="/home" element={<Home products={products} />} />
            <Route path="/product/:id" element={<ProductDetails products={products} />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/add-product" element={<AddProduct />} />
            <Route path="/manage-products" element={<ManageProducts products={products} />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/orders" element={<Orders orders={orders} />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="*" element={<Navigate to="/home" />} />
          </>
        )}
      </Routes>
    </Router>
  );
};

export default App;
