import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Landing from './pages/Landing';
import Register from './pages/Register';
import Login from './pages/Login';
import Home from './pages/Home';
import ProductDetails from './pages/ProductDetails';
import Cart from './pages/Cart';
import AddProduct from './pages/AddProduct';
import ManageProducts from './pages/ManageProducts';
import Dashboard from './pages/Dashboard';
import Orders from './pages/Orders';
import Navbar from './Navbar';
import Profile from './pages/Profile';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);

  const AUTH_SERVICE_URL = 'http://localhost:3000/api/auth';
  const ORDER_SERVICE_URL = 'http://localhost:3002/api/orders';
  const PRODUCT_SERVICE_URL = 'http://localhost:3004/api/products';

  // Function to fetch products
  const fetchProducts = async () => {
    try {
      const response = await fetch(PRODUCT_SERVICE_URL);
      if (!response.ok) throw new Error('Failed to fetch products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Function to fetch orders
  const fetchOrders = async () => {
    try {
      const response = await fetch(ORDER_SERVICE_URL);
      if (!response.ok) throw new Error('Failed to fetch orders');
      const data = await response.json();
      setOrders(data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  // Function to handle user login
  const handleLogin = async (credentials) => {
    try {
      const response = await fetch(`${AUTH_SERVICE_URL}/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(credentials),
      });

      if (!response.ok) throw new Error('Login failed');
      const data = await response.json();
      localStorage.setItem('token', data.token); // Store token
      setIsLoggedIn(true);
      setUserData(data);
    } catch (error) {
      console.error('Error during login:', error);
      alert(error.message); // Notify user of error
    }
  };

  // Function to handle user registration
  const handleRegister = async (userInfo) => {
    try {
      const response = await fetch(`${AUTH_SERVICE_URL}/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userInfo),
      });

      if (!response.ok) throw new Error('Registration failed');
      const data = await response.json();
      localStorage.setItem('token', data.token); // Store token
      setIsLoggedIn(true);
      setUserData(data);
    } catch (error) {
      console.error('Error during registration:', error);
      alert(error.message); // Notify user of error
    }
  };

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token'); // Clear token
    setIsLoggedIn(false);
    setUserData(null);
  };

  // Check for token on initial load
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true);
      // Optionally fetch user data here if needed
    }
  }, []);

  // Fetch products and orders when logged in
  useEffect(() => {
    if (isLoggedIn) {
      fetchProducts();
      fetchOrders();
    }
  }, [isLoggedIn]);

  return (
    <Router>
      {isLoggedIn && <Navbar handleLogout={handleLogout} />}
      <Routes>
        {/* Public Routes */}
        {!isLoggedIn && (
          <>
            <Route path="/" element={<Landing />} />
            <Route path="/register" element={<Register handleRegister={handleRegister} />} />
            <Route path="/login" element={<Login handleLogin={handleLogin} />} />
            <Route path="*" element={<Navigate to="/" />} />
          </>
        )}

        {/* Authenticated Routes */}
        {isLoggedIn && (
          <>
            <Route path="/home" element={<Home products={products} />} />
            <Route path="/product/:id" element={<ProductDetails products={products} />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/add-product" element={<AddProduct />} />
            <Route path="/manage-products" element={<ManageProducts products={products} />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/orders" element={<Orders orders={orders} />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="*" element={<Navigate to="/home" />} />
          </>
        )}
      </Routes>
    </Router>
  );
};

export default App;
